import React from 'react';

import './playfield.scss';

const Playfield = (props) => {
  return (
    <div className='playfield'>
      <div className='dealer__value'></div>
      <div className='bidding'>
        <div className='bidding__bank'>BANK: </div>
        <input
          onChange={(e) => {
            if (this.state.bank - e.target.value >= 0 && e.target.value > 0) {
              props.setBid(e.target.value);
            }
          }}
          className='bidding__input'
          type='number'
        />
        <div className='bidding__submit'>&#10003;</div>
      </div>
      <div className='user-inputs'>
        <button className='btn btn__draw btn__draw-option'>Draw</button>
        <button className='btn btn__new btn__new-option'>Next Round?</button>
        <button className='btn btn__end btn__end-option'>End</button>
      </div>
      <div className='deck'>
        <img
          className='deck__img'
          src={require('../../img/deck.jpg')}
          alt='deck'
        />
      </div>
      <div className='player__value'>0</div>
    </div>
  );
};

export default Playfield;
