import CheckValue from './checkValue';

const DealerDraw = (res, ...props) => {
  console.log(res, props);
  let code = res.data.cards[0].code;
  let val = CheckValue(res.data.cards[0].value, props.dealerValue);
  props.setDealerHand([...props.setDealerHand, code]);
  props.setDealerValue(props.dealerValue + val);
  props.setSubmitted(!props.submitted);
};

export default DealerDraw;
